package com.android.tkengine.elccommerce.UI;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import com.android.tkengine.elccommerce.R;import com.android.tkengine.elccommerce.presenter.CommentPresenter;/** * Created by FangYu on 2016/8/12. */public class CommentsActivity extends AppCompatActivity {    RecyclerView commentsView;    ImageView titleBack,titleMessage;    TextView titleName;    CommentPresenter presenter;    CommentPresenter.MyRecycleViewAdapter adapter;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_comment);        Intent intent = getIntent();        String productId = intent.getStringExtra("productId");        commentsView = (RecyclerView) findViewById(R.id.comment_view);        titleName = (TextView) findViewById(R.id.title_name);        titleBack = (ImageView) findViewById(R.id.title_back);        titleMessage = (ImageView) findViewById(R.id.title_message);        presenter = new CommentPresenter();        adapter = presenter.new MyRecycleViewAdapter(CommentsActivity.this, productId);        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false);        commentsView.setAdapter(adapter);        commentsView.setLayoutManager(linearLayoutManager);        titleBack.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {               finish();            }        });        titleName.setText("评论");    }}