package com.android.tkengine.elccommerce.UI;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.view.ViewPager;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.View;import android.view.WindowManager;import android.widget.ImageView;import android.widget.LinearLayout;import com.android.tkengine.elccommerce.R;import com.android.tkengine.elccommerce.presenter.DisplayPresenter;import java.util.ArrayList;import java.util.List;/** * Created by FangYu on 2016/8/9. */public class DisplayActivity extends AppCompatActivity {    ViewPager goodsPager;    DisplayPresenter presenter;    RecyclerView goodsContent;    DisplayPresenter.MyRecycleViewAdapter myRecycleViewAdapter;    private ImageView titleBack,titleMessage;    List<ImageView> mList;    int[] bannerImages = {R.mipmap.advertise1, R.mipmap.advertise2, R.mipmap.advertise3, R.mipmap.advertise4 };    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_display);        getWindow().addFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS);        getWindow().addFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_NAVIGATION);        initView();    }    private void initView() {        titleBack = (ImageView) findViewById(R.id.title_back);        goodsPager = (ViewPager) findViewById(R.id.goods_pager);        goodsContent = (RecyclerView) findViewById(R.id.goods_content);        List<Integer> datas = new ArrayList();        datas.add(R.mipmap.apple);        datas.add(R.mipmap.advertise1);        datas.add(R.mipmap.advertise2);        mList = new ArrayList<ImageView>();        LinearLayout.LayoutParams params;        for(int i= 0; i<bannerImages.length; i++){            ImageView imageView = new ImageView(DisplayActivity.this);            imageView.setLayoutParams(new LinearLayout.LayoutParams(LinearLayout.LayoutParams.MATCH_PARENT, LinearLayout.LayoutParams.MATCH_PARENT));            imageView.setBackgroundResource(bannerImages[i]);            mList.add(imageView);        }        presenter = new DisplayPresenter();        goodsPager.setAdapter(presenter.new MyGoodsDisAdapter(mList));        presenter.clickBack(DisplayActivity.this, titleBack);        myRecycleViewAdapter = presenter.new MyRecycleViewAdapter(DisplayActivity.this, datas);        goodsContent.setAdapter(myRecycleViewAdapter);        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(this,LinearLayoutManager.VERTICAL, false);        goodsContent.setLayoutManager(linearLayoutManager);    }}