package com.android.tkengine.elccommerce.UI;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.WindowManager;import android.widget.ImageView;import com.android.tkengine.elccommerce.R;import com.android.tkengine.elccommerce.presenter.DisplayPresenter;import java.util.ArrayList;import java.util.List;/** * Created by FangYu on 2016/8/9. */public class DisplayActivity extends AppCompatActivity {    DisplayPresenter presenter;    RecyclerView goods_content;    DisplayPresenter.MyRecycleViewAdapter myRecycleViewAdapter;    private ImageView title_back,title_message;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_display);        getWindow().addFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS);        getWindow().addFlags(WindowManager.LayoutParams.FLAG_TRANSLUCENT_NAVIGATION);        initView();    }    private void initView() {        title_back = (ImageView) findViewById(R.id.title_back);        goods_content = (RecyclerView) findViewById(R.id.goods_content);        List<Integer> datas = new ArrayList();        datas.add(R.mipmap.apple);        datas.add(R.mipmap.advertise1);        datas.add(R.mipmap.advertise2);        presenter = new DisplayPresenter();        presenter.clickBack(DisplayActivity.this, title_back);        myRecycleViewAdapter = presenter.new MyRecycleViewAdapter(DisplayActivity.this, datas);        goods_content.setAdapter(myRecycleViewAdapter);        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(this,LinearLayoutManager.VERTICAL, false);        goods_content.setLayoutManager(linearLayoutManager);    }}