package com.android.tkengine.elccommerce.UI;import android.content.Intent;import android.content.SharedPreferences;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.ImageView;import android.widget.RelativeLayout;import android.widget.TextView;import com.android.tkengine.elccommerce.R;/** * Created by FangYu on 2016/9/10. */public class WalletActivity extends AppCompatActivity {    TextView money;    ImageView back;    RelativeLayout add;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_wallet);        initView();    }    private void initView() {        money = (TextView) findViewById(R.id.wallet_money);        back = (ImageView) findViewById(R.id.wallet_back);        add = (RelativeLayout) findViewById(R.id.add_wallet);        money.setText("0.00");        add.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(WalletActivity.this, AddWalletActivity.class));            }        });        back.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                finish();            }        });    }    @Override    protected void onResume() {        super.onResume();        SharedPreferences sharedPreferences = getSharedPreferences("money",MODE_PRIVATE);        String s = sharedPreferences.getString("money","0.00");        double i = Double.parseDouble(s) + Double.parseDouble(money.getText().toString());        SharedPreferences.Editor editor = getSharedPreferences("money",MODE_PRIVATE).edit();        editor.putString("money",i+"");        editor.commit();        money.setText(i + "");    }}